mapper.select:
select 

from case_group
where case_id = #{caseId,jdbcType=BIGINT}

SELECT top ${pageSize} c.case_id caseId, c.alert_id alertId, c.status, c.statusValue, c.order_id orderId, c.task_Id taskId, c.role_id roleId, c.create_time createDate, c.due_date dueDate, c.score,c.subject, c.amount, c.volume, c.geography, c.scenario, c.link_case linkedCase, c.typeValue, c.userName FROM (SELECT row_number() over(order by ${orderby}) as rownumber,* FROM( SELECT c.case_id,c.alert_id,c.status,(select s.dic_value from common_dic_sub s where c.status = s.dic_key AND s.dic_id =43 AND s.is_deleted = 0) statusValue, c.order_id,c.task_Id,c.role_id,c.create_time,c.update_time,c.due_date, c.score,c.subject,c.amount,c.volume,c.geography,c.scenario,c.link_case, (select s.dic_value from common_dic_sub s where c.case_type = s.dic_key AND s.dic_id =39 AND s.is_deleted = 0) typeValue, (stuff((SELECT ',' + ta.actor_id FROM wf_task_actor ta WHERE ta.task_id = c.task_id FOR xml path ('')),1,1,'')) userName FROM [case_group] c LEFT JOIN wf_hist_order d ON d.order_No = c.case_id LEFT JOIN wf_hist_task ht ON ht.order_Id = d.id LEFT JOIN wf_hist_task_actor wt ON wt.task_Id = ht.id LEFT JOIN wf_task_actor ta ON ta.task_Id = wt.task_id WHERE c.is_deleted = 0 AND c.status NOT IN('11','14','19')
AND (c.case_id like '%${caseId}%' or c.alert_id like '%${caseId}%')
AND wt.actor_Id = #{userName}
AND ta.actor_Id = #{assigner}
AND CONVERT(VARCHAR(100), c.create_time, 111) >= CONVERT(VARCHAR(100), CONVERT(DATETIME, #{createStartDate}), 111)
AND CONVERT(VARCHAR(100), CONVERT(DATETIME, #{createEndDate}), 111) >= CONVERT(VARCHAR(100), c.create_time, 111)
AND CONVERT(VARCHAR(100), c.due_date, 111) >= CONVERT(VARCHAR(100), CONVERT(DATETIME, #{dueStartDate}), 111)
AND CONVERT(VARCHAR(100), CONVERT(DATETIME, #{dueEndDate}), 111) >= CONVERT(VARCHAR(100), c.due_date, 111)
AND c.status = #{status}
AND c.amount >= #{amountMin}
AND #{amountMax} >= c.amount
AND c.volume >= #{volumeMin}
AND #{volumeMax} >= c.volume
AND c.score >= #{scoreMin}
AND #{scoreMax} >= c.score
AND c.geography like ('%'+#{geography}+'%')
AND c.scenario like ('%'+#{scenario}+'%')
GROUP BY c.case_id,c.alert_id,c.status,c.order_id,c.task_Id,c.role_id,c.create_time,c.update_time,c.due_date, c.score,c.subject,c.amount,c.volume,c.geography,c.scenario,c.link_case,c.case_type,c.task_id ) as c) as c WHERE rownumber > ${offset}

SELECT count(0) FROM ( SELECT c.case_id caseId,c.status,c.order_id orderId,c.task_Id taskId,c.role_id roleId,c.create_time createDate,c.due_date dueDate, c.score,c.subject,c.amount,c.volume,c.geography,c.scenario,c.link_case linkedCase, (stuff((SELECT ',' + ta.actor_id FROM wf_task_actor ta WHERE ta.task_id = c.task_id FOR xml path ('')),1,1,'')) userName FROM [case_group] c LEFT JOIN wf_hist_order d ON d.order_No = c.case_id LEFT JOIN wf_hist_task ht ON ht.order_Id = d.id LEFT JOIN wf_hist_task_actor wt ON wt.task_Id = ht.id LEFT JOIN wf_task_actor ta ON ta.task_Id = c.task_id WHERE c.is_deleted = 0 AND c.status NOT IN('11','14','19')
AND (c.case_id like '%${caseId}%' or c.alert_id like '%${caseId}%')
AND wt.actor_Id = #{userName}
AND ta.actor_Id = #{assigner}
AND CONVERT(VARCHAR(100), c.create_time, 111) >= CONVERT(VARCHAR(100), CONVERT(DATETIME, #{createStartDate}), 111)
AND CONVERT(VARCHAR(100), CONVERT(DATETIME, #{createEndDate}), 111) >= CONVERT(VARCHAR(100), c.create_time, 111)
AND CONVERT(VARCHAR(100), c.due_date, 111) >= CONVERT(VARCHAR(100), CONVERT(DATETIME, #{dueStartDate}), 111)
AND CONVERT(VARCHAR(100), CONVERT(DATETIME, #{dueEndDate}), 111) >= CONVERT(VARCHAR(100), c.due_date, 111)
AND c.status = #{status}
AND c.amount >= #{amountMin}
AND #{amountMax} >= c.amount
AND c.volume >= #{volumeMin}
AND #{volumeMax} >= c.volume
AND c.score >= #{scoreMin}
AND #{scoreMax} >= c.score
AND c.geography like ('%'+#{geography}+'%')
AND c.scenario like ('%'+#{scenario}+'%')
GROUP BY c.case_id,c.status,c.order_id,c.task_Id,c.role_id,c.create_time,c.due_date, c.score,c.subject,c.amount,c.volume,c.geography,c.scenario,c.link_case,c.task_id ) as c

SELECT DISTINCT c.case_id caseId, c.alert_id alertId, c.status, (select s.dic_value from common_dic_sub s where c.status = s.dic_key AND s.dic_id =43 AND s.is_deleted = 0) statusValue, c.order_id orderId, c.task_Id taskId, c.role_id roleId, c.create_time createDate, c.update_time updateDate, c.due_date dueDate, c.score, c.subject, c.amount, c.volume, c.geography, c.scenario, c.link_case linkCase, (select s.dic_value from common_dic_sub s where c.case_type = s.dic_key AND s.dic_id =39 AND s.is_deleted = 0) typeValue, (stuff((SELECT ',' + ta.actor_id FROM wf_task_actor ta WHERE ta.task_id = c.task_id FOR xml path ('')),1,1,'')) userName FROM [case_group] c LEFT JOIN wf_hist_order d ON d.order_No = c.case_id LEFT JOIN wf_hist_task ht ON ht.order_Id = d.id LEFT JOIN wf_hist_task_actor wt ON wt.task_Id = ht.id LEFT JOIN wf_task_actor ta ON ta.task_Id = wt.task_id WHERE c.is_deleted = 0 AND c.status NOT IN('11','14','19')
AND (c.case_id like '%${caseId}%' or c.alert_id like '%${caseId}%')
AND CHARINDEX(','+ wt.actor_Id +',' , ','+ #{userName} +',') > 0
AND ta.actor_Id = #{assigner}
AND CONVERT(VARCHAR(100), c.create_time, 111) >= CONVERT(VARCHAR(100), CONVERT(DATETIME, #{createStartDate}), 111)
AND CONVERT(VARCHAR(100), CONVERT(DATETIME, #{createEndDate}), 111) >= CONVERT(VARCHAR(100), c.create_time, 111)
AND CONVERT(VARCHAR(100), c.due_date, 111) >= CONVERT(VARCHAR(100), CONVERT(DATETIME, #{dueStartDate}), 111)
AND CONVERT(VARCHAR(100), CONVERT(DATETIME, #{dueEndDate}), 111) >= CONVERT(VARCHAR(100), c.due_date, 111)
AND c.status = #{status}
AND c.amount >= #{amountMin}
AND #{amountMax} >= c.amount
AND c.volume >= #{volumeMin}
AND #{volumeMax} >= c.volume
AND c.score >= #{scoreMin}
AND #{scoreMax} >= c.score
AND c.geography like ('%'+#{geography}+'%')
AND c.scenario like ('%'+#{scenario}+'%')
order by ${orderby}

SELECT c.status,d.dic_value,d.dic_key,COUNT(1) AS [alertCount] FROM case_group c LEFT JOIN common_dic_sub d ON d.dic_key = c.status AND d.is_deleted = 0 AND d.dic_id = 43 LEFT JOIN wf_task_actor w ON w.task_Id = c.task_id WHERE c.is_deleted = 0 AND c.task_id != '' AND c.status not in (11,14,19)
AND CHARINDEX(','+w.actor_Id+',' , ','+#{actorId}+',') > 0
AND CHARINDEX(','+w.actor_Id+',' , ','+#{param}+',') > 0
AND c.create_time > #{createStartDate}
AND c.create_time < #{createEndDate}
GROUP BY c.status,d.dic_value,d.dic_key

SELECT sum(DateDiff( hh, t.create_Time, t.finish_Time )) as [time] FROM case_group c LEFT JOIN wf_task_approval p ON c.order_Id = p.order_id LEFT JOIN wf_hist_task t ON t.id = p.task_id LEFT JOIN wf_task_actor w ON w.task_Id = c.task_id WHERE c.status in (11,14,19)
AND p.role_id = #{roleId}
AND w.actor_Id IN (#{actorId})
